import React from "react";
import axios from "axios";
import "./App.css";
class App extends React.Component {
  state = {
    newTask: "",
    tabLists: [
      {
        name: "phrase1",
        done: true
      },
      {
        name: "phrase2",
        done: false
      },
      {
        name: "phrase3",
        done: true
      }
    ]
  };

  async componentDidMount() {
    const response = await axios.get("http://localhost:3001/");
    console.log(response.data);
    this.setState({
      tabLists: response.data
      //isLoading: false,
    });
  }
  render() {
    return (
      <div>
        <h1> => TO Do LiSt &lt;= </h1>
        <ul className="list">
          {this.state.tabLists.map((element, index) => {
            if (element.done === true) {
              return (
                <div className="cross" key={index}>
                  <button
                    onClick={() => {
                      let tabList2 = [...this.state.tabLists];
                      tabList2.splice(index, 1);
                      this.setState({ tabLists: tabList2 });
                    }}
                  >
                    X
                  </button>
                  <li
                    onClick={() => {
                      let tabList2 = [...this.state.tabLists];
                      tabList2[index].done = !tabList2[index].done;
                      // if(tabList2[index].done === false) {
                      //   tabList2[index].done = true
                      // } else {
                      //   tabList2[index].done = false
                      // }

                      this.setState({ tabList: tabList2 });
                    }}
                    className="task-done list"
                  >
                    {element.name}
                  </li>
                </div>
              );
            } else {
              return (
                <div className="cross" key={index}>
                  <button
                    onClick={() => {
                      let tabList2 = [...this.state.tabLists];
                      tabList2.splice(index, 1);
                      this.setState({ tabLists: tabList2 });
                    }}
                  >
                    X
                  </button>
                  <li
                    onClick={() => {
                      let tabList2 = [...this.state.tabLists];
                      tabList2[index].done = !tabList2[index].done;
                      // if(tabList2[index].done === false) {
                      //   tabList2[index].done = true
                      // } else {
                      //   tabList2[index].done = false
                      // }

                      this.setState({ tabList: tabList2 });
                    }}
                    className="list"
                  >
                    {element.name}
                  </li>
                </div>
              );
            }
          })}
        </ul>
        <input
          value={this.state.newTask}
          onChange={e => this.setState({ newTask: e.target.value })}
        />
        <button
          onClick={async () => {
            axios.post("http://localhost:3001/create", {
              name: this.state.newTask
            });
            this.setState({ newTask: "" });

            // let tabList2 = [...this.state.tabLists];
            // tabList2.push({ name: this.state.newTask, done: false });
            // this.setState({ tabLists: tabList2, newTask: "" });
          }}
        >
          OK
        </button>
      </div>
    );
  }
}

export default App;
