import React from "react";
import "./App.css";
class App extends React.Component {
  state = {
    newTask: "",
    tabLists: [
      {
        name: "phrase1",
        done: false
      },
      {
        name: "phrase2",
        done: true
      },
      {
        name: "phrase3",
        done: false
      }
    ]
  };
  render() {
    return (
      <div>
        <h1> => TO Do LiSt &lt;= </h1>
        <ul>
          {this.state.tabLists.map((valeur, index) => {
            return <li key={index}>{valeur.name}</li>;
          })}
        </ul>
        <input
          newTask={this.state.newTask}
          onChange={e => this.setState({ newTask: e.target.value })}
        />
        <button
          onClick={() => {
            const tabList2 = [...this.state.tabLists];
            tabList2.push({ name: this.state.newTask, done: false });
            this.setState({ tabLists: tabList2, newTask: "" });
          }}
        >
          OK
        </button>
      </div>
    );
  }
}

export default App;
