import React from "react";
import axios from "axios";
import "./App.css";

class App extends React.Component {
  state = {
    newUrl: "",
    tabLists: [
      {
        website: "web1"
      },
      {
        website: "web2"
      },
      {
        website: "web3"
      }
    ]
  };

  async componentDidMount() {
    try {
      this.update();
    } catch (error) {
      alert("problem");
    }
  }
  update = async () => {
    const response = await axios.get("http://localhost:3001/");
    console.log(response.data);
    this.setState({
      tabLists: response.data
      //isLoading: false,
    });
  };

  render() {
    return (
      <div>
        <h1>Simplify your links</h1>

        <input
          value={this.state.newUrl}
          onChange={e => this.setState({ newUrl: e.target.value })}
        />
        <button
          onClick={async () => {
            await axios.post("http://localhost:3001/create", {
              name: this.state.newUrl
            });
            this.setState({ newUrl: "" });
            this.update();
          }}
        >
          SHORTEN URL
        </button>

        <ul className="list">
          {this.state.tabLists.map((element, index) => {
            if (element.done === true) {
              return (
                <div key={index}>
                  <li
                    onClick={() => {
                      let tabList2 = [...this.state.tabLists];
                      tabList2[index].done = !tabList2[index].done;

                      this.setState({ tabList: tabList2 });
                    }}
                    className="url-done list"
                  >
                    {element.name}
                    {element._id}
                  </li>
                </div>
              );
            } else {
              return (
                <div className="cross" key={index}>
                  <li
                    onClick={() => {
                      let tabList2 = [...this.state.tabLists];

                      this.setState({ tabList: tabList2 });
                    }}
                    className="list"
                  >
                    {element.name}
                  </li>
                </div>
              );
            }
          })}
        </ul>
      </div>
    );
  }
}

export default App;
