import React from "react";
import axios from "axios";
import "./App.css";

class App extends React.Component {
  state = {
    newUrl: "",
    loading: false,
    tabLists: [],
    visits: 0
  };

  async componentDidMount() {
    try {
      this.setState({
        loading: true
      });
      this.update();
    } catch (error) {
      alert("problem");
    }
  }
  update = async () => {
    const response = await axios.get("http://localhost:3001/");
    console.log(response.data);
    this.setState({
      tabLists: response.data,
      visits: response.data.visits
    });
  };

  render() {
    return (
      <div>
        <section className="title">
          <h1>Simplify your links</h1>
          <div className="input-button">
            <input
              type="text"
              className="form-control"
              value={this.state.newUrl}
              onChange={e => this.setState({ newUrl: e.target.value })}
            />
            <button
              className="button-control"
              onClick={async () => {
                await axios.post("http://localhost:3001/create", {
                  url: this.state.newUrl
                });

                this.setState({ newUrl: "" });
                this.update();
              }}
            >
              SHORTEN URL
            </button>
          </div>
        </section>
        <div className="container">
          <ul>
            <li className="navLine">
              <p>0riginal URL</p>
              <p>Short URL</p>
              <p>visits</p>
            </li>
          </ul>
          <ul>
            {this.state.tabLists.map((element, index) => {
              return (
                <li
                  className="line"
                  key={index}
                  onClick={() => {
                    let tabList2 = [...this.state.tabLists];
                    tabList2[index].done = !tabList2[index].done;

                    this.setState({ tabList: tabList2 });
                  }}
                >
                  {/* <p>0riginal URL</p> */}
                  <div className="urlBar">
                    <a href={element.url}>
                      <p>{element.url}</p>
                    </a>
                  </div>
                  {/* <p>Short URL</p> */}
                  <div className="shortBar">
                    <a href={element.short_url}>
                      <p>{element.short_url}</p>
                    </a>
                  </div>
                  {/* <p>visits</p> */}
                  <div className="visitsBar">{element.visits}</div>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    );
  }
}

export default App;
