import React from "react";
import "./App.css";
class App extends React.Component {
  state = {
    newTask: "",
    tabLists: [
      {
        name: "phrase1",
        done: false
      },
      {
        name: "phrase2",
        done: true
      },
      {
        name: "phrase3",
        done: false
      }
    ]
  };
  render() {
    return (
      <div>
        <h1> => TO Do LiSt &lt;= </h1>
        <ul>
          {this.state.tabLists.name.map(valeur => {
            return <div>{valeur.name}</div>;
          })}
        </ul>
        <input
          newTask={this.state.newTask}
          onChange={e => this.setState({ newTask: e.target.value })}
        />
        <button
          onClick={() => {
            let tabList2 = [...this.state.tabLists.name];
            tabList2.push(this.state.newTask);
            this.setState({ tabLists: tabList2 });
          }}
        >
          OK
        </button>
      </div>
    );
  }
}

export default App;
