import React from "react";
import "./App.css";
class App extends React.Component {
  state = {
    newTask: "",
    tabLists: ["<3", "Danser", "<3"]
  };
  render() {
    return (
      <div>
        <h1> => TO Do LiSt &lt;= </h1>
        <ul>
          {this.state.tabLists.map(value => {
            return <div>{value}</div>;
          })}
        </ul>
        <input
          newTask={this.state.newTask}
          onChange={e => this.setState({ newTask: e.target.value })}
        />
        <button
          onClick={() => {
            let tabList2 = [...this.state.tabLists];
            tabList2.push(this.state.newTask);
            this.setState({ tabLists: tabList2 });
          }}
        >
          OK
        </button>
        <button
          onClick={() => {
            this.state.taskRemove.split();
          }}
        />
      </div>
    );
  }
}

export default App;
