import React from "react";
import axios from "axios";
import "./App.css";

class App extends React.Component {
  state = {
    newUrl: "",
    loading: false,
    tabLists: [],
    visits: 0
  };

  async componentDidMount() {
    try {
      this.setState({
        loading: true
      });
      this.update();
    } catch (error) {
      alert("problem");
    }
  }
  update = async () => {
    const response = await axios.get("http://localhost:3001/");
    console.log(response.data);
    this.setState({
      tabLists: response.data,
      visits: response.data.visits
    });
  };

  render() {
    return (
      <div>
        <section className="title">
          <h1>Simplify your links</h1>
          <div className="input-button">
            <input
              type="text"
              className="form-control"
              value={this.state.newUrl}
              onChange={e => this.setState({ newUrl: e.target.value })}
            />
            <button
              className="button-control"
              onClick={async () => {
                await axios.post("http://localhost:3001/create", {
                  url: this.state.newUrl
                });

                this.setState({ newUrl: "" });
                this.update();
              }}
            >
              SHORTEN URL
            </button>
          </div>
        </section>
        <div className="container">
          <ul>
            <li className="navLine">
              <p>0riginal URL</p>
              <p>Short URL</p>
              <p>visits</p>
            </li>
          </ul>
          <div className="box">
            {this.state.tabLists.map((element, index) => {
              return (
                <ul className="modelUrl">
                  <li className="boxUrl">
                    <a href={element.url}>
                      <p>{element.url}</p>
                      {/* {element.short_url} */}
                      <p>{element.short_url}</p>
                      {element.visits}
                      <p>{element.visits}</p>
                    </a>
                  </li>
                  {/* <li className="boxShort">
                    <a href={element.short_url}>{element.short_url}</a>
                  </li>
                  <li className="boxVisits">
                    <a href={element.visits}>{element.visits}</a>
                  </li> */}
                </ul>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
