import React from "react";
import axios from "axios";
import "./App.css";

class App extends React.Component {
  state = {
    newUrl: "",
    loading: false,
    tabLists: []
  };

  async componentDidMount() {
    try {
      this.setState({
        loading: true
      });
      this.update();
    } catch (error) {
      alert("problem");
    }
  }
  update = async () => {
    const response = await axios.get("http://localhost:3001/");
    console.log(response.data);
    this.setState({
      tabLists: response.data
    });
  };

  render() {
    return (
      <div>
        <h1>Simplify your links</h1>
        <input
          value={this.state.newUrl}
          onChange={e => this.setState({ newUrl: e.target.value })}
        />
        <button
          onClick={async () => {
            await axios.post("http://localhost:3001/create", {
              url: this.state.newUrl
            });

            this.setState({ newUrl: "" });
            this.update();
          }}
        >
          SHORTEN URL
        </button>

        <ul className="list">
          {this.state.tabLists.map((element, index) => {
            return (
              <li
                className="line"
                key={index}
                onClick={() => {
                  let tabList2 = [...this.state.tabLists];
                  tabList2[index].done = !tabList2[index].done;

                  this.setState({ tabList: tabList2 });
                }}
              >
                <a href={element.url}>{element.url}</a>
                <a href={element.short_url}>{element.short_url}</a>

                {element.visits}
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}

export default App;
